mapper.update:
UPDATE chat_members
SET last_seen_at = #{now}
WHERE user_id = #{userId}
AND chat_id = #{chatId}

mapper.select:
SELECT user_id
FROM chat_members
WHERE chat_id = #{chatId}
  AND EXISTS (
    SELECT *
    FROM chat_members
    WHERE user_id = #{userId}
      AND chat_id = #{chatId}
);

SELECT IF(COUNT(*) > 0, 1, 0)
FROM chat_members
WHERE user_id = #{reqId}
  AND chat_id IN (
      SELECT chat_id
      FROM chat_members
      WHERE user_id = #{id}
    )
