mapper.select:
SELECT id, asset_name, original_name, content_type, file_size, category, message_id, status, raw_asset_hash, normalized_asset_hash, create_time, update_time
FROM assets
WHERE message_id = #{messageId}

mapper.update:
UPDATE assets SET status = #{status},
category = #{category},
update_time = NOW() WHERE asset_name = #{assetName}

mapper.select:
SELECT id, asset_name, original_name, content_type, file_size, category, message_id, status, raw_asset_hash, normalized_asset_hash, create_time, update_time
FROM assets
WHERE status = 0 AND create_time < #{beforeTime}
ORDER BY create_time ASC
LIMIT #{limit} OFFSET #{offset}
