mapper.select:
SELECT id, chat_id, invite_code, code_hash, expires_at, max_uses, used_count, revoked, created_by, create_time, update_time
FROM chat_invites
WHERE code_hash = #{codeHash}

mapper.update:
UPDATE chat_invites
SET revoked = 1,
    update_time = NOW()
WHERE chat_id = #{chatId}
  AND id = #{inviteId}
  AND revoked = 0
