mapper.select:
SELECT
    COUNT(m.id)
FROM chat_members cm
         JOIN messages m ON cm.chat_id = m.chat_id
WHERE cm.user_id = #{userId}
  AND cm.chat_id = #{chatId}
  AND m.sender_id != #{userId}
  AND m.create_time > IFNULL(cm.last_seen_at, '1970-01-01 00:00:00')

SELECT u.uid AS sender, c.chat_code AS chatCode, m.seq_id AS seqId, m.type AS type, m.text, m.asset_ids AS assetIds, m.create_time AS createTime FROM messages m JOIN chats c ON m.chat_id = c.id JOIN users u ON m.sender_id = u.id WHERE m.chat_id = #{chatId}
AND m.seq_id < #{cursorSeqId}
ORDER BY m.seq_id DESC LIMIT 30
